import React, { useEffect, useState } from "react";
import { validateEmail, validatePhone } from "../utils/validators";

const initialState = {
  name: "",
  email: "",
  phone: "",
  address: "",
};

const ContactForm = ({ onSubmit, editable }) => {
  const [contact, setContact] = useState(initialState);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (editable) {
      setContact(editable);
    }
  }, [editable]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setContact((prev) => ({ ...prev, [name]: value }));
  };

  const validate = () => {
    const newErrors = {};
    if (!contact.name.trim()) newErrors.name = "Name is required";
    if (!validateEmail(contact.email)) newErrors.email = "Invalid email";
    if (!validatePhone(contact.phone))
      newErrors.phone = "Phone must be 10 digits";
    if (!contact.address.trim()) newErrors.address = "Address is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      onSubmit(contact);
      setContact(initialState);
      setErrors({});
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form">
      <h2>{editable ? "Edit Contact" : "Add Contact"}</h2>

      <input
        type="text"
        name="name"
        placeholder="Name"
        value={contact.name}
        onChange={handleChange}
      />
      {errors.name && <small className="error">{errors.name}</small>}

      <input
        type="email"
        name="email"
        placeholder="Email"
        value={contact.email}
        onChange={handleChange}
      />
      {errors.email && <small className="error">{errors.email}</small>}

      <input
        type="text"
        name="phone"
        placeholder="Phone Number"
        value={contact.phone}
        onChange={handleChange}
      />
      {errors.phone && <small className="error">{errors.phone}</small>}

      <input
        type="text"
        name="address"
        placeholder="Address"
        value={contact.address}
        onChange={handleChange}
      />
      {errors.address && <small className="error">{errors.address}</small>}

      <button type="submit">{editable ? "Update" : "Add"}</button>
    </form>
  );
};

export default ContactForm;
