// src/App.jsx
import React, { useState, useEffect } from "react";
import ContactForm from "./components/ContactForm";
import ContactList from "./components/ContactList";
import SearchBar from "./components/SearchBar";
import { getContacts, saveContacts } from "./localStorage";
import "./styles/App.css";

function App() {
  const [contacts, setContacts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [editableContact, setEditableContact] = useState(null);

  useEffect(() => {
    const storedContacts = getContacts();
    setContacts(storedContacts);
  }, []);

  useEffect(() => {
    saveContacts(contacts);
  }, [contacts]);

  useEffect(() => {
    const storedContacts = getContacts();
    console.log("Loaded from localStorage:", storedContacts); // Debug log
    setContacts(storedContacts);
  }, []);

  const addOrUpdateContact = (contact) => {
    if (contact.id) {
      setContacts(contacts.map((c) => (c.id === contact.id ? contact : c)));
    } else {
      setContacts([...contacts, { ...contact, id: crypto.randomUUID() }]);
    }
  };

  const deleteContact = (id) => {
    setContacts(contacts.filter((contact) => contact.id !== id));
  };

  const handleEdit = (contact) => {
    setEditableContact(contact);
  };

  const filteredContacts = contacts.filter(
    (c) =>
      c.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      c.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="container">
      <h1>Contact Manager</h1>
      <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
      <ContactForm onSubmit={addOrUpdateContact} editable={editableContact} />
      <ContactList
        contacts={filteredContacts}
        onDelete={deleteContact}
        onEdit={handleEdit}
      />
    </div>
  );
}

export default App;
